        mcmaster.addDepartment(science);

        // Create Buildings and Rooms
        Building jhe = new Building();
        Room room101 = new Room();
        Room room202 = new Room();
        jhe.addRoom(room101);
        jhe.addRoom(room202);
        mcmaster.addBuilding(jhe);

        // Create Professors (extends Person)
        Professor profDavid = new Professor();
