        // Create Courses with Assignments and Textbooks
        Course softwareDesign = new Course();
        Assignment assignment1 = new Assignment();
        Assignment assignment2 = new Assignment();
        softwareDesign.addAssignment(assignment1);
        softwareDesign.addAssignment(assignment2);

        Textbook designPatterns = new Textbook();
        softwareDesign.setTextbook(designPatterns);

        // Department offers courses
        engineering.addCourse(softwareDesign);

        // Professor teaches courses
        profDavid.addCourse(softwareDesign);

        // Create Students (extends Person)
        Student student1 = new Student();
        Student student2 = new Student();

        // Students enroll in courses
        student1.addCourse(softwareDesign);
        student2.addCourse(softwareDesign);

        // Students receive grades
        Grade gradeA = new Grade();
