        System.out.println("Engineering professors: " + engineering.getProfessors().size());
        System.out.println("Software Design assignments: " + softwareDesign.getAssignments().size());
        System.out.println("Software Design textbook: " + softwareDesign.getTextbook());
        System.out.println("Professor teaches courses: " + profDavid.getCourses().size());
        System.out.println("Student 1 courses: " + student1.getCourses().size());
        System.out.println("Student 1 grades: " + student1.getGrades().size());
